#include <bits/stdc++.h>#define rep(i,n) for(int i=0;i<(int)n;i++)using namespace std;typedef long long ll;ll mod=1e9+7;typedef vector<ll> vi;typedef vector<vi> mat;ll ex(ll x,ll p){	ll a=1;	while(p){		if(p%2) a=a*x%mod;		x=x*x%mod;		p/=2;	}	return a;}ll inv(ll a){	return ex(a,mod-2);}const int MAX_N=10;ll det(mat a){	int N=a.size();	ll ans=1;	rep(i,N){		if(a[i][i]==0){			for(int j=i+1;j<N;j++){				if(a[j][i]!=0){					for(int k=i;k<N;k++) swap(a[i][k],a[j][k]);					ans*=-1;	//swap					break;				}			}			if(a[i][i]==0) return 0;		}		for(int j=i+1;j<N;j++){			ll c=a[j][i]*inv(a[i][i])%mod;			for(int k=i;k<N;k++){				a[j][k]=(a[j][k]-c*a[i][k])%mod;			}		}		ans=ans*a[i][i]%mod;	}	if(ans<0) ans+=mod;	return ans;}int main(){	int N;	cin>>N;	mat a(N,vi(N,0));	rep(i,N) rep(j,N) cin>>a[i][j];	cout<<det(a)<<endl;}